mapper.update:
UPDATE we_customer_messgaeResult


        send_time=#{sendTime},


        status=#{status}



    message_id=#{messageId}

        AND chat_id=#{chatId}


        AND external_userid=#{externalUserid}

mapper.select:
SELECT user_name, GROUP_CONCAT(IFNULL(external_name,chat_name) SEPARATOR '„ÄÅ') customers FROM we_customer_messgaeResult
message_id=#{messageId}
AND `status`=#{status}
GROUP BY user_name
