mapper.select:

and wecode.code_type = #{codeType}
and wecode.is_join_confirm_friends = #{isJoinConfirmFriends}

    and wecode.activity_scene like concat('%', #{activityScene}, '%')

and wecode.welcome_msg = #{welcomeMsg}

    AND wecode.create_by like concat('%', #{createBy}, '%')


    AND date_format(wecode.create_time,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


    AND date_format(wecode.create_time,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


    AND wecusn.mobile like concat('%', #{mobile}, '%')


    AND wecusn.business_name like concat('%', #{useUserName}, '%')

group by wecode.id

mapper.update:
update we_emple_code
code_type = #{codeType}, is_join_confirm_friends = #{isJoinConfirmFriends}, activity_scene = #{activityScene}, welcome_msg = #{welcomeMsg}, create_by = #{createBy}, create_time = #{createTime}, del_flag = #{delFlag}, config_id = #{configId}, qr_code = #{qrCode}, media_id = #{mediaId},
where id = #{id}

mapper.select:
select wecode.id as emple_code_id, wecode.welcome_msg , wm.category_id, wm.material_url, wm.content, wm.material_name, wm.digest, wm.cover_url from we_emple_code wecode left join we_emple_code_use_scop wecus on wecode.id = wecus.emple_code_id left join we_material wm on wm .id = wecode.media_id where wecode.del_flag = 0
and wecode.activity_scene = #{activityScene}
and wecus.business_id = #{userId}
