mapper.select:
select date_format(stat_time, '%Y-%m-%d') as x_time, IFNULL(sum(new_apply_cnt),0) as new_apply_cnt, IFNULL(sum(new_contact_cnt),0) as new_contact_cnt, IFNULL(sum(chat_cnt),0) as chat_cnt, IFNULL(sum(message_cnt),0) as message_cnt, IFNULL(sum(reply_percentage),0) as reply_percentage, IFNULL(sum(avg_reply_time),0) as avg_reply_time, IFNULL(sum(negative_feedback_cnt),0) as negative_feedback_cnt from we_user_behavior_data
date_format(stat_time, '%Y-%m-%d') = date_format(#{dateTime}, '%Y-%m-%d');
YEARWEEK(date_format(stat_time,'%Y-%m-%d')) = YEARWEEK(date_format(#{dateTime}, '%Y-%m-%d'));
DATE_FORMAT(stat_time,'%Y-%m') = DATE_FORMAT(#{dateTime}, '%Y-%m');
YEAR(stat_time) = YEAR(#{dateTime});

SELECT tbl._date AS x_time, IFNULL(tbr.new_apply_cnt,0) as new_apply_cnt, IFNULL(tbr.new_contact_cnt,0) as new_contact_cnt, IFNULL(tbr.chat_cnt,0) as chat_cnt, IFNULL(tbr.message_cnt,0) as message_cnt, IFNULL(tbr.reply_percentage,0) as reply_percentage, IFNULL(tbr.avg_reply_time,0) as avg_reply_time, IFNULL(tbr.negative_feedback_cnt,0) as negative_feedback_cnt FROM (SELECT (@s:=@s+1) AS _index,STR_TO_DATE(DATE(DATE_SUB(CURRENT_DATE,INTERVAL @s DAY)),'%Y-%m-%d') AS _date FROM information_schema.CHARACTER_SETS,(SELECT @s:=-1) AS init WHERE @s < #{few} ORDER BY _date) AS tbl LEFT JOIN(SELECT sum(ifnull(wubd.new_apply_cnt, 0)) new_apply_cnt, sum(ifnull(wubd.new_contact_cnt, 0)) new_contact_cnt, sum(ifnull(wubd.chat_cnt, 0)) chat_cnt, sum(ifnull(wubd.message_cnt, 0)) message_cnt, sum(ifnull(wubd.reply_percentage, 0)) reply_percentage, sum(ifnull(wubd.avg_reply_time, 0)) avg_reply_time, sum(ifnull(wubd.negative_feedback_cnt, 0)) negative_feedback_cnt,
STR_TO_DATE(DATE_FORMAT(wubd.stat_time,'%Y-%m-%d'),'%Y-%m-%d') AS finish_date
FROM we_user_behavior_data wubd where STR_TO_DATE(DATE_FORMAT(wubd.stat_time,'%Y-%m-%d'),'%Y-%m-%d') >= #{startTime}
AND STR_TO_DATE(DATE_FORMAT(wubd.stat_time,'%Y-%m-%d'),'%Y-%m-%d') <= #{endTime}
GROUP BY finish_date
ORDER BY finish_date
) AS tbr ON tbl._date = tbr.finish_date GROUP BY tbl._date;
